"runc\",","    \"storage-driver\": \"overlay2\",","    \"metrics-addr\": \"127.0.0.1:9323\",","    \"builder\": {","        \"gc\": {","            \"enabled\": true,","            \"defaultKeepStorage\": \"20GB\"","        }","    }","}","EOF","","# After Docker daemon.json configuration","print_message \"${YELLOW}\" \"Testing Docker configuration...\"","if ! docker info \u0026\u003e/dev/null; then","  print_error \"Docker failed to start. Checking configuration...\"","  journalctl -u docker.service --no-pager | tail -n 50","  exit 1","fi","","systemctl enable docker","systemctl restart docker || {","  print_error \"Docker failed to start. Logs:\"","  journalctl -u docker.service --no-pager | tail -n 50","  exit 1","}","","# Verify Docker configuration","print_message \"${YELLOW}\" \"Verifying Docker configuration...\"","docker info | grep -E \"Cgroup Driver|Storage Driver|Logging Driver\"","","# --- User Setup ---","print_message \"${YELLOW}\" \"Creating docker user...\"","adduser --system --group --shell /bin/bash --home /home/docker --disabled-password docker","usermod -aG docker docker","","# --- SSH Configuration ---","","print_message \"${YELLOW}\" \"Configuring SSH...\"","mkdir -p /home/docker/.ssh","chown -R docker:docker /home/docker","chmod 755 /home/docker","","# Copy root's authorized keys to docker user if they exist","if [ -f /root/.ssh/authorized_keys ]; then","  cp /root/.ssh/authorized_keys /home/docker/.ssh/authorized_keys","  chown -R docker:docker /home/docker/.ssh","  chmod 700 /home/docker/.ssh","  chmod 600 /home/docker/.ssh/authorized_keys","fi","","cat \u003c\u003cEOF \u003e/etc/ssh/sshd_config","Include /etc/ssh/sshd_config.d/*.conf","","Port 22","AddressFamily inet","Protocol 2","","HostKey /etc/ssh/ssh_host_ed25519_key","HostKey /etc/ssh/ssh_host_ecdsa_key","HostKey /etc/ssh/ssh_host_rsa_key","","SyslogFacility AUTH","LogLevel VERBOSE","","LoginGraceTime 30","PermitRootLogin prohibit-password","StrictModes yes","MaxAuthTries 10","MaxSess